SELECT a.id, b.id taskId, b.`name` taskName, e.id lineId, e.`name` lineName, a.finish_info finishInfo, a.pause_record pauseRecord, a.report, a.state, (SELECT g.text FROM dict g WHERE g.type='reception_state' AND g.`value`=a.state)stateText, a.operation_user defectUser, f.`name` defectUserName FROM defect_task_reception a INNER JOIN defect_task b ON a.defect_task_id = b.id INNER JOIN inspection_reception c ON b.inspection_reciption_id = c.id INNER JOIN inspection d ON c.inspection_id = d.id INNER JOIN line e ON d.line_id = e.id INNER JOIN `user` f ON a.operation_user = f.username
AND e.id = #{condition.lineId}
AND e.name LIKE CONCAT('%', #{condition.lineName}, '%')
AND a.state = #{condition.state}
AND f.username = #{condition.user}
UNION SELECT a.id, b.id taskId, b.`name` taskName, e.id lineId, e.`name` lineName, a.finish_info finishInfo, a.pause_record pauseRecord, a.report, a.state, (SELECT g.text FROM dict g WHERE g.type='reception_state' AND g.`value`=a.state)stateText, a.operation_user defectUser, f.`name` defectUserName FROM defect_task_reception a INNER JOIN defect_task b ON a.defect_task_id = b.id INNER JOIN inspection_reception c ON b.inspection_reciption_id = c.id INNER JOIN inspection d ON c.inspection_id = d.id INNER JOIN line e ON d.line_id = e.id INNER JOIN `user` f ON a.operation_user = f.username
AND e.id = #{condition.lineId}
AND e.name LIKE CONCAT('%', #{condition.lineName}, '%')
AND a.state = #{condition.state}
AND f.name LIKE CONCAT('%', #{condition.user}, '%')
